// Definitions to replace missing vite/client types
declare module '*.svg' {
  import * as React from 'react';
  export const ReactComponent: React.FunctionComponent<React.SVGProps<SVGSVGElement> & { title?: string }>;
  const src: string;
  export default src;
}

declare module '*.png' {
  const content: string;
  export default content;
}

declare module '*.jpg' {
  const content: string;
  export default content;
}

declare module '*.jpeg' {
  const content: string;
  export default content;
}

declare module '*.gif' {
  const content: string;
  export default content;
}

// Ensure process.env.API_KEY is typed for the Google GenAI SDK as per guidelines
declare const process: {
  env: {
    API_KEY: string;
    [key: string]: string | undefined;
  }
};
